var btnOpen=document.querySelector(".btn-mail"),popup=document.querySelector(".modal-content"),close=document.querySelector(".modal-content-close"),login=popup.querySelector("[name=login]");btnOpen.addEventListener("click",function(a){a.preventDefault(),popup.classList.add("modal-content-show"),login.focus()}),close.addEventListener("click",function(a){a.preventDefault(),popup.classList.remove("modal-content-show")}),window.addEventListener("keydown",function(a){27==a.keyCode&&popup.classList.contains("modal-content-show")&&popup.classList.remove("modal-content-show")});